show databases;
create schema tb_al;
use tb_al;
select database();
drop schema tb_al;
SHOW TABLES;

CREATE TABLE funcionarios (
    id_documento VARCHAR(20) PRIMARY KEY, 
    apellidos_nombres VARCHAR(255) NOT NULL,
    telefono_institucional VARCHAR(20) NOT NULL,
    profesion VARCHAR(100) NOT NULL,
    perfil TEXT NOT NULL,
    cargo VARCHAR(100) NOT NULL,
    decreto VARCHAR(100) NOT NULL,
    enlace_sigep VARCHAR(255) NOT NULL,
    correo_electronico_institucional VARCHAR(255) NOT NULL UNIQUE, 
    direccion VARCHAR(255) NOT NULL,
    horario_trabajo VARCHAR(100) NOT NULL,
    foto_funcionario VARCHAR(255), 
    fecha_ingreso DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    
    CHECK (CHAR_LENGTH(apellidos_nombres) > 5),
    CHECK (correo_electronico_institucional LIKE '%@%')
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

drop table funcionarios;
select * from funcionarios;


SELECT 
    id_documento AS id,  
    apellidos_nombres,
    telefono_institucional,
    profesion,
    perfil,
    cargo,
    decreto,
    enlace_sigep,
    correo_electronico_institucional,
    direccion,
    horario_trabajo,
    foto_funcionario AS enlace_foto
FROM funcionarios;